import React, { useState } from 'react';
import axios from 'axios';

const ProposalForm = () => {
  const [formData, setFormData] = useState({
    clientInfo: [],
    projectDetails: [],
    pricingAndCosts: '',
  });
  const [proposal, setProposal] = useState('');
  const [generatePDF, setGeneratePDF] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => {
      const newData = { ...prevData };
      if (name === 'pricingAndCosts') {
        newData[name] = value;
      } else {
        newData[name][e.target.dataset.index] = value;
      }
      return newData;
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log('Form data submitted:', formData);
    try {
      const response = await axios.post('/api/generate/proposal', { ...formData, generatePDF });
      console.log('Response from backend:', response.data);
      if (generatePDF) {
        const url = window.URL.createObjectURL(new Blob([response.data], { type: 'application/pdf' }));
        const link = document.createElement('a');
        link.href = url;
        link.setAttribute('download', 'proposal.pdf');
        document.body.appendChild(link);
        link.click();
        link.parentNode.removeChild(link);
      } else {
        setProposal(response.data.proposal);
      }
    } catch (error) {
      console.error('Error generating proposal:', error);
      alert(`Failed to generate proposal: ${error.response.data.details}`);
    }
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <div>
          <h2>Client Information</h2>
          {['What is the name of the client?', 'What is the client\'s industry?', 'What is the size of the client\'s company (e.g., small, medium, large)?', 'What is the client\'s contact information (email, phone number)?', 'Who is the primary contact person for the project?'].map((question, index) => (
            <div key={index}>
              <label>{question}</label>
              <input type="text" name="clientInfo" data-index={index} onChange={handleChange} />
            </div>
          ))}
        </div>
        <div>
          <h2>Project Details</h2>
          {['What is the primary goal of this project?', 'What specific problems or needs does this project aim to address?', 'Why is this project important for the client?'].map((question, index) => (
            <div key={index}>
              <label>{question}</label>
              <input type="text" name="projectDetails" data-index={index} onChange={handleChange} />
            </div>
          ))}
        </div>
        <div>
          <h2>Pricing and Costs</h2>
          <label>What is the predefined hourly rate for this project in NOK?</label>
          <input type="text" name="pricingAndCosts" data-index={0} onChange={handleChange} />
        </div>
        <div>
          <label>
            <input type="checkbox" checked={generatePDF} onChange={() => setGeneratePDF(!generatePDF)} />
            Generate PDF
          </label>
        </div>
        <button type="submit">Generate Proposal</button>
      </form>
      {proposal && !generatePDF && (
        <div>
          <h2>Generated Proposal</h2>
          <div dangerouslySetInnerHTML={{ __html: proposal }} />
        </div>
      )}
    </div>
  );
};

export default ProposalForm;
