import React, { useState } from 'react';
import axios from 'axios';

const ProposalForm = () => {
  const [formData, setFormData] = useState({});
  const [generatePDF, setGeneratePDF] = useState(false);
  const [proposal, setProposal] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log('Form data submitted:', formData);
    try {
      const response = await axios.post('/api/generate/proposal', { ...formData, generatePDF }, { responseType: generatePDF ? 'arraybuffer' : 'json' });
      console.log('Response from backend:', response.data);
      if (generatePDF) {
        const blob = new Blob([response.data], { type: 'application/pdf' });
        const url = window.URL.createObjectURL(blob);
        const link = document.createElement('a');
        link.href = url;
        link.setAttribute('download', 'proposal.pdf');
        document.body.appendChild(link);
        link.click();
        link.parentNode.removeChild(link);
      } else {
        setProposal(response.data.proposal);
      }
    } catch (error) {
      console.error('Error generating proposal:', error);
      alert(`Failed to generate proposal: ${error.response.data.details}`);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <label>
          Client Name:
          <input type="text" name="clientName" onChange={handleInputChange} />
        </label>
        {/* Add other form fields here */}
        <label>
          <input type="checkbox" checked={generatePDF} onChange={(e) => setGeneratePDF(e.target.checked)} />
          Generate PDF
        </label>
        <button type="submit">Generate Proposal</button>
      </form>
      {proposal && (
        <div>
          <h2>Generated Proposal</h2>
          <div dangerouslySetInnerHTML={{ __html: proposal.replace(/\n/g, '<br />') }} />
        </div>
      )}
    </div>
  );
};

export default ProposalForm;
