import React, { useState, useEffect } from 'react';
import axios from 'axios';
import questions from '../config/questions';

const ProposalForm = () => {
  const [formData, setFormData] = useState({});
  const [proposal, setProposal] = useState('');

  // Load saved form data from local storage when the component mounts
  useEffect(() => {
    const savedFormData = localStorage.getItem('formData');
    if (savedFormData) {
      setFormData(JSON.parse(savedFormData));
    }
  }, []);

  // Save form data to local storage whenever it changes
  useEffect(() => {
    localStorage.setItem('formData', JSON.stringify(formData));
  }, [formData]);

  const handleChange = (category, index, event) => {
    const newFormData = { ...formData };
    if (!newFormData[category]) {
      newFormData[category] = [];
    }
    newFormData[category][index] = event.target.value;
    setFormData(newFormData);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log('Form data submitted:', formData);
    try {
      const response = await axios.post('/api/generate/proposal', formData);
      console.log('Response from backend:', response.data);
      setProposal(response.data.proposal);
      alert('Proposal generated successfully.');
    } catch (error) {
      console.error('Error generating proposal:', error);
    }
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        {questions.map((section, sectionIndex) => (
          <div key={sectionIndex}>
            <h3>{section.category}</h3>
            {section.questions.map((question, questionIndex) => (
              <div key={questionIndex}>
                <label>{question}</label>
                <input
                  type="text"
                  value={formData[section.category]?.[questionIndex] || ''}
                  onChange={(e) => handleChange(section.category, questionIndex, e)}
                />
              </div>
            ))}
          </div>
        ))}
        <button type="submit">Generate Proposal</button>
      </form>
      {proposal && (
        <div>
          <h2>Generated Proposal</h2>
          <pre>{proposal}</pre>
        </div>
      )}
    </div>
  );
};

export default ProposalForm;
