// client/src/config/questions.js
const questions = [
    {
      category: 'Client Information',
      questions: [
        'What is the name of the client?',
        'What is the client\'s industry?',
        'What is the size of the client\'s company (e.g., small, medium, large)?',
        'What is the client\'s contact information (email, phone number)?',
        'Who is the primary contact person for the project?'
      ]
    },
    {
      category: 'Project Details',
      questions: [
        'What is the primary goal of this project?',
        'What specific problems or needs does this project aim to address?',
        'How does the current process work, and what are its limitations?',
        'Why is this project important for the client?'
      ]
    },
    {
      category: 'Project Objectives and Deliverables',
      questions: [
        'What are the main objectives of this project?',
        'What specific deliverables are expected from this project?',
        'What functionalities should the MVP include?',
        'What are the key success criteria for this project?'
      ]
    },
    {
      category: 'Project Timeline and Phases',
      questions: [
        'What are the different phases of this project?',
        'What activities are involved in each phase?',
        'What is the estimated time required for each phase?',
        'What is the overall timeline for the project?'
      ]
    },
    {
      category: 'Pricing and Costs',
      questions: [
        'What is the predefined hourly rate for this project?',
        'Are there any additional costs or fees to consider?',
        'What is the total estimated cost of the project based on the hourly rate and estimated hours?',
        'Are there any cost-saving options available (e.g., discounts for long-term maintenance contracts)?',
        'What are the payment terms and schedule?'
      ]
    },
    {
      category: 'Phase 1: Planning and Specification',
      questions: [
        'What are the key activities during the planning and specification phase?',
        'How will the client’s requirements be gathered and analyzed?',
        'What documentation will be produced during this phase?'
      ]
    },
    {
      category: 'Phase 2: Design and Development',
      questions: [
        'What are the main activities during the design and development phase?',
        'How will the system architecture be designed?',
        'What are the requirements for the user interface (UI) design?',
        'How will the database structure be designed and implemented?',
        'What backend functionalities need to be developed?',
        'What frontend functionalities need to be developed?',
        'How will the integration between frontend and backend be handled?',
        'Are there any specific requirements for media handling (e.g., image upload and display)?'
      ]
    },
    {
      category: 'Phase 3: Testing',
      questions: [
        'What types of testing will be performed (e.g., unit testing, integration testing, user acceptance testing)?',
        'How will the test plans be developed?',
        'What criteria will be used to determine the success of testing?'
      ]
    },
    {
      category: 'Phase 4: Implementation and Training',
      questions: [
        'What activities are involved in the implementation phase?',
        'How will the system be deployed to the production environment?',
        'What training will be provided to the client’s staff?',
        'What documentation will be provided for the system?'
      ]
    },
    {
      category: 'Phase 5: Evaluation and Maintenance',
      questions: [
        'How will feedback be collected from the users after deployment?',
        'What processes will be in place for making adjustments and fixing issues?',
        'How will ongoing maintenance and support be handled?',
        'What are the plans for future updates and enhancements?'
      ]
    },
    {
      category: 'Summary and Additional Information',
      questions: [
        'What are the key benefits of this project for the client?',
        'Are there any potential risks or challenges associated with this project?',
        'How will the project outcomes be measured and evaluated?',
        'What additional information or considerations should be included in the proposal?'
      ]
    }
  ];
  
  export default questions;
