// client/src/App.js
import React from 'react';
import { BrowserRouter as Router, Route, Switch, Link } from 'react-router-dom';
import Settings from './components/Settings';
import TemplateSelection from './components/TemplateSelection';
import Questions from './components/Questions';
import ReportGeneration from './components/ReportGeneration';
import './App.css';

function App() {
  return (
    <div className="App">
      <header className="App-header">
        <img src={`${process.env.PUBLIC_URL}/Logo2_bg.png`} className="App-logo" alt="logo" />
        <nav>
          <ul className="nav-links">
            <li><Link to="/">Home</Link></li>
            <li><Link to="/settings">Settings</Link></li>
            <li><Link to="/templates">Templates</Link></li>
            <li><Link to="/questions">Questions</Link></li>
            <li><Link to="/report">Report</Link></li>
          </ul>
        </nav>
      </header>
      <Router>
        <Switch>
          <Route path="/" exact component={() => <h2>Welcome to AI Automated Proposals</h2>} />
          <Route path="/settings" component={Settings} />
          <Route path="/templates" component={TemplateSelection} />
          <Route path="/questions" component={Questions} />
          <Route path="/report" component={ReportGeneration} />
        </Switch>
      </Router>
    </div>
  );
}

export default App;
